let initialState = {
    isAuthenticated: false,
    email: ''
}

export const setState = (state = initialState, action) => {
    switch(action.type) {
        case 'FLIP-STATUS':
            let state = {
                ...state
            };
            if(state.isAuthenticated) {
                state.isAuthenticated = false;
                state.email = '';
            }
            else {
                state.isAuthenticated = true;
                state.email = action.payload
            }
            break;
    }
    return state;
};
