import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';

import Spinner from '../Spinner/Spinner';

import './Login.css';

class Login extends Component {
    state = {
        loading: false
    }

    sendDetails() {

    }

    render() {
        let renderingComponent = null;
        if(this.state.loading) {
            renderingComponent = <Spinner />
        }
        else {
            renderingComponent = (
                <form className="login-form" onSubmit={this.sendDetails}>
                    <div className="form-control">
                        <label for="email">E-Mail</label>
                        <input type="email" name="email" id="email" value={this.state.email}></input>
                    </div>
                    <div className="form-control">
                        <label for="password">Password</label>
                        <input type="password" name="password" id="password" value={this.state.password}></input>
                    </div>
                    <button class="btn" type="submit">Login</button>
                </form>
            );
        }

        return {renderingComponent};
    }
}

export default Login;
