import React, { Component } from 'react';
import { connect } from 'react-redux';

import homePage from '../../icons/homepage.jpg';

import NavBar from '../NavBar/NavBar';

import './HomePage.css';

class HomePage extends Component {
    render() {
        let displayHome = true;
        let displayJoin = this.props.isAuthenticated;
        let displayLogin = !this.props.isAuthenticated;
        let displaySignUp = !this.props.isAuthenticated;
        let displayLogout = this.props.isAuthenticated;

        return (
            <div>
                <NavBar 
                    displayHome={displayHome} 
                    displayJoin={displayJoin}  
                    displayLogin={displayLogin} 
                    displaySignUp={displaySignUp}
                    displayLogout={displayLogout} />
                <div className="homeImage">  
                    <img src={homePage} alt="Home Page Image" />
                </div>
            </div>
        );
    }
};

const mapStateToProps = (state) => {
    return {
        isAuthenticated: state.isAuthenticated
    };
};

export default connect(mapStateToProps, null)(HomePage);
